export function sortProducts(catalog, sortOrder) {
  const sortedProducts = catalog._products.slice().sort((a, b) => {
    if (sortOrder === "less") {
      return a.price - b.price;
    } else if (sortOrder === "more") {
      return b.price - a.price;
    }
    return 0;
  });

  return {
    ...catalog,
    products: sortedProducts,
  };
}
